#!/bin/bash\n\n# Example usage of the eRechnung API with curl\n\nAPI_BASE=\"http://127.0.0.1:8080\"\n\necho \"üè• Health Check\"\necho \"===============\"\ncurl -s \"$API_BASE/health\" | jq .\necho -e \"\\n\"\n\nif [ \"$1\" != \"\" ]; then\n    if [ -f \"$1\" ]; then\n        echo \"üìÑ Extracting XML from: $1\"\n        echo \"===========================\"\n        curl -s -X POST -F \"file=@$1\" \"$API_BASE/extract_xml\" | jq .\n    else\n        echo \"‚ùå Error: File '$1' does not exist\"\n    fi\nelse\n    echo \"üìã Usage Examples\"\n    echo \"=================\"\n    echo \"Health check:\"\n    echo \"  curl $API_BASE/health\"\n    echo \"\"\n    echo \"Extract XML from PDF:\"\n    echo \"  curl -X POST -F 'file=@invoice.pdf' $API_BASE/extract_xml\"\n    echo \"\"\n    echo \"Use this script:\"\n    echo \"  $0 path/to/your/invoice.pdf\"\nfi\n